import React, { useState, useEffect } from "react";
import axios from "axios";
import { v4 as uuidv4 } from "uuid";
import "./App.css";

const API_URL = "http://127.0.0.1:8000";

const App = () => {
  const [gameId, setGameId] = useState(null);
  const [board, setBoard] = useState(Array(9).fill(" "));
  const [currentPlayer, setCurrentPlayer] = useState("x"); // AI is X
  const [winner, setWinner] = useState(null);
  const [gameOver, setGameOver] = useState(false);

  const startNewGame = async () => {
    try {
      const newId = uuidv4();
      setGameId(newId);

      const response = await axios.post(`${API_URL}/new-game`, { game_id: newId });
      setBoard(response.data.board);
      setWinner(response.data.winner);
      setGameOver(response.data.game_over);

      // If AI is X, it moves first
      if (response.data.current_player === "x") {
        setCurrentPlayer("x");
        await handleAIMove(newId, response.data.board);
      } else {
        setCurrentPlayer("o");
      }
    } catch (error) {
      console.error("Error starting new game:", error);
      alert("Cannot connect to backend. Make sure FastAPI is running.");
    }
  };

  const handleAIMove = async (id, currentBoard) => {
    try {
      const response = await axios.post(`${API_URL}/make-move`, {
        game_id: id,
        position: null, // Let backend decide AI move
      });
      setBoard(response.data.board);
      setCurrentPlayer("o");
      setWinner(response.data.winner);
      setGameOver(response.data.game_over);
    } catch (error) {
      console.error("Error making AI move:", error);
    }
  };

  const handleMove = async (pos) => {
    if (gameOver || board[pos] !== " " || currentPlayer !== "o") return;

    try {
      const response = await axios.post(`${API_URL}/make-move`, {
        game_id: gameId,
        position: pos,
      });

      setBoard(response.data.board);
      setWinner(response.data.winner);
      setGameOver(response.data.game_over);

      // AI turn
      if (!response.data.game_over) {
        setCurrentPlayer("x");
        await handleAIMove(gameId, response.data.board);
      }
    } catch (error) {
      console.error("Error making move:", error);
      alert("Cannot connect to backend. Make sure FastAPI is running.");
    }
  };

  const renderCell = (i) => (
    <button
      key={i}
      onClick={() => handleMove(i)}
      className="cell"
      disabled={board[i] !== " " || gameOver || currentPlayer !== "o"}
    >
      {board[i]}
    </button>
  );

  useEffect(() => {
    startNewGame();
  }, []);

  return (
    <div className="app">
      <h1>Tic Tac Toe</h1>
      <div className="board">{board.map((_, i) => renderCell(i))}</div>
      {gameOver ? (
        <h2>
          {winner === 1
            ? "AI (X) wins!"
            : winner === -1
            ? "You (O) win!"
            : "Draw!"}
        </h2>
      ) : (
        <h2>Current Turn: {currentPlayer.toUpperCase()}</h2>
      )}
      <button className="new-game" onClick={startNewGame}>
        New Game
      </button>
    </div>
  );
};

export default App;
